package br.com.api.controller;import br.com.api.dto.DTOConsultarHoleriteSimples;import br.com.api.dto.DTOFuncionarioSimples;import br.com.api.dto.DTOHoleriteCompleto;import br.com.api.models.Holerite;import br.com.api.repository.HoleriteRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.format.annotation.DateTimeFormat;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.time.LocalDate;import java.util.Optional;import java.util.List;import java.util.stream.Collectors;@RestController@RequestMapping("/api/holerites")public class HoleriteController {    @Autowired    private HoleriteRepository holeriteRepository;    @GetMapping("/{idHolerite}")    public ResponseEntity<DTOHoleriteCompleto> buscarHoleritePorId(@PathVariable Long idHolerite){        Optional<Holerite> holerite = holeriteRepository.findById(idHolerite);        if(holerite.isEmpty()){            return ResponseEntity.notFound().build();        }        return ResponseEntity.ok(new DTOHoleriteCompleto(holerite.get()));    }    @GetMapping("/consultar-holerites/{cpf}")    public ResponseEntity<List<DTOConsultarHoleriteSimples>> buscarHolerites(@PathVariable String cpf){        List<Holerite> holerites = holeriteRepository.findAllByFuncionarioCpf(cpf);        if(holerites.isEmpty()){            return ResponseEntity.notFound().build();        }        List<DTOConsultarHoleriteSimples> DTOHolerites = holerites.stream().map(holerite ->                new DTOConsultarHoleriteSimples(holerite)).collect(Collectors.toList());        return ResponseEntity.ok(DTOHolerites);    }    @GetMapping("/consultar-holerite/cpf={cpf}&data={dataHolerite}")    public ResponseEntity<DTOHoleriteCompleto> buscarHolerite(@PathVariable String cpf,    @PathVariable @DateTimeFormat(pattern = "yyyy-MM-dd") LocalDate dataHolerite){        Optional<Holerite> holerite = holeriteRepository.buscarHolerite(cpf, dataHolerite);        if(holerite.isEmpty()){            return ResponseEntity.notFound().build();        }        return ResponseEntity.ok(new DTOHoleriteCompleto(holerite.get()));    }}