package br.com.api.controller;import br.com.api.models.Holerite;import br.com.api.repository.HoleriteRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.Optional;@RestController@RequestMapping("/api/holerites")public class HoleriteController {    @Autowired    private HoleriteRepository holeriteRepository;    @GetMapping("/{idHolerite}")    public ResponseEntity<Holerite> buscarHoleritePorId(@PathVariable Long idHolerite){        Optional<Holerite> holerite = holeriteRepository.findById(idHolerite);        if(holerite.isEmpty()){            return ResponseEntity.notFound().build();        }        return ResponseEntity.ok(holerite.get());    }}