package br.com.api.dto;import br.com.api.models.*;import java.time.LocalDate;import java.util.List;public class DTOConsultarHoleriteSimples {    private Long id;    private LocalDate dataDoHolerite;    public DTOConsultarHoleriteSimples(Holerite holerite) {        this.id = holerite.getId();        this.dataDoHolerite = holerite.getCabecalho().getData();    }    public Long getId() {        return id;    }    public LocalDate getDataDoHolerite() {        return dataDoHolerite;    }}