package br.com.api.dto;import br.com.api.models.Empresa;public class DTOEmpresa {    private Long id;    private String razaoSocial;    private String cnpj;    public DTOEmpresa(Empresa empresa) {        this.id = empresa.getId();        this.razaoSocial = empresa.getRazaoSocial();        this.cnpj = empresa.getCnpj();    }    public Long getId() {        return id;    }    public String getRazaoSocial() {        return razaoSocial;    }    public String getCnpj() {        return cnpj;    }}