package br.com.api.dto;import br.com.api.models.Funcionario;import java.math.BigDecimal;public class DTOFuncionarioCompleto {    private Long id;    private String nome;    private BigDecimal salario;    private String funcao;    private BigDecimal adicionalPericulosidade;    private int jornadaDeTrabalho;    private int horasExtras;    private int dependentes;    private BigDecimal ajudaDeCusto;    private BigDecimal valorPlanoDeSaude;    private String cpf;    public DTOFuncionarioCompleto(Funcionario funcionario) {        this.id = funcionario.getId();        this.nome = funcionario.getNome();        this.salario = funcionario.getSalario();        this.funcao = funcionario.getFuncao();        this.adicionalPericulosidade = funcionario.getAdicionalPericulosidade();        this.jornadaDeTrabalho = funcionario.getJornadaDeTrabalho();        this.horasExtras = funcionario.getHorasExtras();        this.dependentes = funcionario.getDependentes();        this.ajudaDeCusto = funcionario.getAjudaDeCusto();        this.valorPlanoDeSaude = funcionario.getValorPlanoDeSaude();        this.cpf = funcionario.getCpf();    }    public Long getId() {        return id;    }    public String getNome() {        return nome;    }    public BigDecimal getSalario() {        return salario;    }    public String getFuncao() {        return funcao;    }    public BigDecimal getAdicionalPericulosidade() {        return adicionalPericulosidade;    }    public int getJornadaDeTrabalho() {        return jornadaDeTrabalho;    }    public int getHorasExtras() {        return horasExtras;    }    public int getDependentes() {        return dependentes;    }    public BigDecimal getAjudaDeCusto() {        return ajudaDeCusto;    }    public BigDecimal getValorPlanoDeSaude() {        return valorPlanoDeSaude;    }    public String getCpf() {        return cpf;    }}