package br.com.api.dto;import br.com.api.models.Funcionario;public class DTOFuncionarioSimples {    private Long id;    private String nome;    private String funcao;    public DTOFuncionarioSimples(Funcionario funcionario) {        this.id = funcionario.getId();        this.nome = funcionario.getNome();        this.funcao = funcionario.getFuncao();    }    public Long getId() {        return id;    }    public String getNome() {        return nome;    }    public String getFuncao() {        return funcao;    }}