package br.com.api.dto;import br.com.api.models.*;import java.util.List;public class DTOHoleriteCompleto {    private Long id;    private Cabecalho cabecalho;    private List<Detalhe> detalhes;    private Rodape rodape;    public DTOHoleriteCompleto(Holerite holerite) {        this.id = holerite.getId();        this.cabecalho = holerite.getCabecalho();        this.detalhes = holerite.getDetalhes();        this.rodape = holerite.getRodape();    }    public Long getId() {        return id;    }    public Cabecalho getCabecalho() {        return cabecalho;    }    public List<Detalhe> getDetalhes() {        return detalhes;    }    public Rodape getRodape() {        return rodape;    }}